'use client'

// src/toggle.tsx
import * as Slot from "@rn-primitives/slot";
import * as React from "react";
import { Pressable as Pressable2 } from "react-native";
var Root = React.forwardRef(
  ({ asChild, pressed, onPressedChange, disabled, onPress: onPressProp, ...props }, ref) => {
    function onPress(ev) {
      if (disabled) return;
      const newValue = !pressed;
      onPressedChange(newValue);
      onPressProp?.(ev);
    }
    const Component = asChild ? Slot.Pressable : Pressable2;
    return <Component
      ref={ref}
      aria-disabled={disabled}
      role='switch'
      aria-selected={pressed}
      onPress={onPress}
      accessibilityState={{
        selected: pressed,
        disabled
      }}
      disabled={disabled}
      {...props}
    />;
  }
);
Root.displayName = "RootNativeToggle";
export {
  Root
};
