'use client'

// src/toggle.web.tsx
import * as Toggle from "@radix-ui/react-toggle";
import * as Slot from "@rn-primitives/slot";
import * as React from "react";
import { Pressable as Pressable2 } from "react-native";
var Root2 = React.forwardRef(
  ({ asChild, pressed, onPressedChange, disabled, onPress: onPressProp, ...props }, ref) => {
    function onPress(ev) {
      onPressProp?.(ev);
      onPressedChange(!pressed);
    }
    const Component = asChild ? Slot.Pressable : Pressable2;
    return <Toggle.Root pressed={pressed} onPressedChange={onPressedChange} disabled={disabled} asChild><Component ref={ref} onPress={onPress} disabled={disabled} role='button' {...props} /></Toggle.Root>;
  }
);
Root2.displayName = "RootWebToggle";
export {
  Root2 as Root
};
